import axios from "axios";
import { createContext, useContext, useEffect, useState } from "react";
import { YT_API_URL, YT_WATCH_URL, TRANSLATE_URL } from "../API";

const AppContext = createContext(null);

export const useAppContext = () => {
    const context = useContext(AppContext);
    if (!context) {
        throw new Error('useAppcontext must be within appContextProvider!');
    }
    return context;
};

const AppContextProvider = ({ children }) => {
    const [videos, setVideos] = useState([]);
    const [language, setLanguage] = useState('en'); // State for language
    const [translatedIntro, setTranslatedIntro] = useState('');
    const [translatedAbout, setTranslatedAbout] = useState('');

    useEffect(() => {
        const fetchVideos = async () => {
            try {
                const response = await axios.get(`${YT_API_URL}&part=snippet&type=video&maxResults=16`);
                setVideos(response.data.items);
            } catch (error) {
                console.error("Error fetching videos:", error);
            }
        };

        fetchVideos();
    }, []);

    useEffect(() => {
        const translateText = async (text, targetLang) => {
            try {
                const response = await axios.post(TRANSLATE_URL, {
                    auth_key: "826a1567-f15f-4e39-8f20-6d4a894ed920", // Add your DeepL API key here
                    text: text,
                    target_lang: targetLang,
                });
                return response.data.translations[0].text;
            } catch (error) {
                console.error("Translation error:", error);
            }
        };

        const translateContent = async () => {
            const intro = "Welcome to our Financial Literacy Resource Hub! ..."; // Original intro text
            const about = "This project was motivated by the clear financial literacy gap ..."; // Original about text

            setTranslatedIntro(await translateText(intro, language));
            setTranslatedAbout(await translateText(about, language));
        };

        translateContent();
    }, [language]); // Re-run translation if the language changes

    return (
        <AppContext.Provider value={{ videos, language, setLanguage, translatedIntro, translatedAbout }}>
            {children}
        </AppContext.Provider>
    );
};

export { AppContextProvider };
