import axios from "axios";
import { createContext, useContext, useEffect } from "react";
import { useState } from "react";
import { YT_API_URL, YT_WATCH_URL } from "../API";
import { Link } from "react-router-dom";

const AppContext = createContext(null);

export const useAppContext = () => {
    const context = useContext(AppContext);
    if (!context) {
        throw new Error('useAppcontext must be within appContextProvider!');
    };
    return context;
};


const AppContextProvider = ({ children, searchTerm }) => {
    const [videos, setVideos] = useState([]);

    // Fetch video list on component mount or when searchTerm changes
    useEffect(() => {
        const fetchVideos = async () => {
            try {
                const response = await axios.get(`${YT_API_URL}&q=${encodeURIComponent(searchTerm)}&part=snippet&type=video&maxResults=16`);
                console.log("API response", response.data);
                setVideos(response.data.items); 
            } catch (error) {
                console.error("Error fetching videos:", error);
            }
        };

        fetchVideos();
    }, [searchTerm])

    // Fetch translations when language changes
    // useEffect(() => {
    //     const translateContent = async () => {
    //         const introContent = "Welcome to our Financial Literacy Resource Hub! Our mission is to provide detailed, easy-to-understand information on critical financial topics such as bank accounts, credit, taxes, budgeting, student loans and financing, insurance, and investing.";
    //         const aboutContent = "This project was also motivated by the clear financial literacy gap seen in different communities. Research has shown that Black and Hispanic individuals, both in their youth and adulthood, tend to have lower financial literacy rates compared to their counterparts.";

    //         try {
    //             const introRes = await axios.post("https://libretranslate.com/translate", {
    //                 q: introContent,
    //                 source: "en",
    //                 target: language,
    //                 format: "text"
    //             });
    //             setTranslatedIntro(introRes.data.translatedText);

    //             const aboutRes = await axios.post("https://libretranslate.com/translate", {
    //                 q: aboutContent,
    //                 source: "en",
    //                 target: language,
    //                 format: "text"
    //             });
    //             setTranslatedAbout(aboutRes.data.translatedText);

    //         } catch (error) {
    //             console.error("Error fetching translations:", error);
    //         }
    //     };

    //     translateContent();
    // }, [language]);

    return (
        <AppContext.Provider value={{ videos}}>
            {children}
        </AppContext.Provider>
    );
};

const VideoList = () => {
    const { videos } = useAppContext([]); // Access videos from context

    return (
        <div className="video-list">
            {videos.map((video, index) => (
                <div className="video" key={index}>
                    <div className="video-item title">
                        <h2>{video.snippet?.title}</h2> {/* Accessing video title */}
                    </div>
                    <Link to={`${YT_WATCH_URL}${video.id.videoId}`}>
                        <div className="video-item thumbnail">
                            <img src={video.snippet?.thumbnails.medium.url} alt="thumbnail" />
                        </div>
                    </Link>
                </div>
            ))}
        </div>
    );
};

export { AppContextProvider, VideoList };
